pragma solidity ^0.6.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/cryptography/ECDSA.sol";

interface CompoundContractInterface {
  function mint(uint mintAmount) external returns (uint);
  function redeem(uint redeemTokens) external returns (uint);
  function balanceOf(address guy) external view returns (uint);
}

contract CompoundChannel {
  using ECDSA for bytes32;

//  State Variables
  address payable public sender;
  address payable public recipient;
  uint256 public endTime;
  IERC20 public token;
  CompoundContractInterface cToken;
  address public factoryAddress;

//   Events
    // Need to add events
//   event FundsDeposited(address depositor, uint256 amount, address TokenAddress);

  constructor(
    address payable _sender,
    address payable _recipient,
    uint256 _endTime,
    address _tokenAddress,
    address _cTokenAddress,
    address _factoryAddress
    ) public {
    sender = _sender;
    recipient = _recipient;
    endTime = _endTime;
    token = IERC20(_tokenAddress);
    factoryAddress = _factoryAddress;
    cToken = CompoundContractInterface(_cTokenAddress);
  }

  function depositFunds() public payable returns(bool) {
    uint256 tokenBalance = token.balanceOf(address(this));
    assert(tokenBalance > 0);

    require(token.approve(address(cToken), tokenBalance), 'approval error');
    require(cToken.mint(tokenBalance) == 0, 'minting error');
    // emit FundsDeposited(msg.sender, _depositAmount, address(token));
    return true;
  }

  function forceClose() public {
    require(now > endTime);
    require(msg.sender == sender);
    uint256 cTokenBalance = cToken.balanceOf(address(this));
    require(cToken.redeem(cTokenBalance) == 0, "redeem error");
    uint256 balance = token.balanceOf(address(this));
    token.transfer(sender, balance);
  }

  function close(uint256 _value, bytes memory _signature) public {
    require(msg.sender == recipient);

    bytes32 hash = keccak256(abi.encodePacked(_value, address(this))).toEthSignedMessageHash();
    address signer = hash.recover(_signature);
    require(signer == sender);

    // Redeem Tokens
    uint256 cTokenBalance = cToken.balanceOf(address(this));
    require(cToken.redeem(cTokenBalance) == 0, "redeem error");

    uint256 balance = token.balanceOf(address(this));
    uint256 toRecipient = balance < _value ? balance : _value;
    require(token.transfer(recipient, toRecipient), "transfer error");

    if (toRecipient < balance) token.transfer(sender, balance - toRecipient);
  }
}

contract CompoundChannelFactory {
  using ECDSA for bytes32;
  // State Variables
  mapping(address => address[]) public channelRegistery;

  // Signature Information
  uint256 constant chainId = 42;
  struct Payment {
    uint256 amount;
  }
  string private constant PAYMENT_TYPEHASH = "Payment(uin256 amount)";
  bytes32 constant salt = 0xf2e421f4a3edcb9b1111d503bfe733db1e3f6cdc2b7971ee739626c97e86a558;
  string private constant EIP712_DOMAIN_TYPEHASH = "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract,bytes32 salt)";

  // Events
  event ChannelCreated(address channelAddress, address sender);

  function createChannel(
    address payable _recipient,
    uint256 _endTime,
    uint256 _depositAmount,
    address _tokenAddress,
    address _cTokenAddress
    ) public returns(bool) {

    IERC20 token = IERC20(_tokenAddress);
    // Creates new Channel Contract
    CompoundChannel compChan = new CompoundChannel(
      msg.sender,
      _recipient,
      _endTime,
      _tokenAddress,
      _cTokenAddress,
      address(this) // Factory Address
      );

    // Transfers funds from allowance to the new contract
    require(token.transferFrom(msg.sender, address(compChan), _depositAmount), 'transferFrom error');
    // Has the new contract mint cTokens
    require(compChan.depositFunds(), 'depositFunds error');  //issue with revert

    // Record new channel information
    channelRegistery[msg.sender].push(address(compChan));
    emit ChannelCreated(address(compChan), msg.sender);
    return true;
  }

  function hashPayment(
    Payment memory payment,
    address _channelAddress
    ) private pure returns (bytes32){

    bytes32 DOMAIN_SEPARATOR = keccak256(abi.encode(
    EIP712_DOMAIN_TYPEHASH,
    keccak256("Compound Channels"),
    keccak256("42"),
    chainId,
    _channelAddress,
    salt
    ));

    return keccak256(abi.encodePacked(
      "\\x19\\x01",
      DOMAIN_SEPARATOR,
      keccak256(abi.encode(
        PAYMENT_TYPEHASH,
        payment.amount
      ))
    ));
  }

  function checkSignature(
    address _sender,
    address _channelAddress,
    address _checker, // the msg.sender of the original tx
    address _recipient,
    Payment memory payment,
    uint8 sigV,
    bytes32 sigR,
    bytes32 sigS
    ) public pure returns (bool) {
      require(_recipient == _checker);
      return _sender == ecrecover(hashPayment(payment, _channelAddress), sigV, sigR, sigS);
  }
}
